export const formatDistance = (kilometers) => {
    if (kilometers === null || kilometers === undefined) return '';

    const meters = kilometers * 1000;
    if (kilometers < 1) {
      return `${Math.round(meters)}m`;
    }
    return `${kilometers.toFixed(1)}km`;
  };
