import React from 'react';
import { formatDistance } from '../utils/formatters';
import { FiActivity, FiMapPin } from 'lucide-react'; // lucide-react 아이콘 사용

const SearchPanel = ({
  suggestions,
  onSelectFacility,
  onSelectItem,
  searchText,
  setSearchText
}) => {
  if (!suggestions) return null;

  return (
    <div className="search-panel">
      {suggestions?.mainItems?.length > 0 && (
        <div className="search-section">
          <h3 className="text-sm font-semibold mb-2">
            <span className="flex items-center">
              <FiActivity className="mr-2" size={16} />
              종목
            </span>
          </h3>
          {suggestions.mainItems.map((item, index) => (
            <div
              key={index}
              className="search-item flex items-center p-2 hover:bg-gray-100 cursor-pointer"
              onClick={() => onSelectItem(item)}
            >
              <span className="text-sm">{item}</span>
            </div>
          ))}
        </div>
      )}

      {suggestions?.facilities?.length > 0 && (
        <div className="search-section">
          <h3 className="text-sm font-semibold mb-2">
            <span className="flex items-center">
              <FiMapPin className="mr-2" size={16} />
              시설명
            </span>
          </h3>
          {suggestions.facilities
            .filter(facility => 'location_on'.includes(facility.fcltyNm))
            .map((facility) => (
              <div
                key={facility.id}
                className="search-item flex items-center p-2 hover:bg-gray-100 cursor-pointer"
                onClick={() => onSelectFacility(facility)}
              >
                <div className="ml-2">
                  <div>{facility.fcltyNm.replace('location_on', '')}</div>
                  <div className="text-sm text-gray-500">
                    {formatDistance(facility.distance)}
                  </div>
                </div>
              </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SearchPanel;
