// ChatContainer.js
import React, { useState, useEffect, useRef } from 'react';
import { MessageBubble } from './MessageBubble';
import { ChatInput } from './ChatInput';

const ChatContainer = () => {
  const [messages, setMessages] = useState([]);
  const [threadId, setThreadId] = useState(null);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    const startChat = async () => {
      try {
        const response = await fetch('http://localhost:8080/chat/start', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
        });
        const data = await response.json();
        setThreadId(data.id);

        // 초기 메시지 추가
        setMessages([{
          id: 'initial',
          type: 'assistant',
          content: '안녕하세요! AI 누리입니다. 실시간으로 유익한 맞춤형 답변을 제공해요'
        }]);
      } catch (error) {
        console.error('Chat initialization failed:', error);
      }
    };
    startChat();
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async (text) => {
    if (!threadId || !text.trim()) return;

    // 사용자 메시지 추가
    setMessages(prev => [...prev, {
      id: Date.now(),
      type: 'user',
      content: text
    }]);

    try {
      // 메시지 전송
      await fetch('http://localhost:8080/chat/send', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          threadId,
          prompt: text
        })
      });

      // 응답 조회
      const response = await fetch(`http://localhost:8080/chat?threadId=${threadId}`);
      const data = await response.json();

      // 새 메시지 추가
      const lastMessage = data.data[data.data.length - 1];
      setMessages(prev => [...prev, {
        id: lastMessage.id,
        type: 'assistant',
        content: lastMessage.content[0].text.value
      }]);
    } catch (error) {
      console.error('Failed to send message:', error);
    }
  };

  return (
    <div className="flex flex-col h-full bg-gray-100">
      <div className="flex-1 overflow-y-auto p-4">
        {messages.map(message => (
          <MessageBubble 
            key={message.id}
            type={message.type}
            content={message.content}
          />
        ))}
        <div ref={messagesEndRef} />
      </div>
      <ChatInput onSend={sendMessage} />
    </div>
  );
};

export default ChatContainer;
