// hooks/useMarkerClusterer.js
import { useRef, useEffect } from 'react';
import { MarkerClusterer } from "@googlemaps/markerclusterer";

// SVG 클러스터 아이콘 생성 함수
const createSvgClusterIcon = (size, color, opacity) => {
  const svg = `
    <svg width="${size}" height="${size}" viewBox="0 0 ${size} ${size}" xmlns="http://www.w3.org/2000/svg">
      <circle 
        cx="${size/2}" 
        cy="${size/2}" 
        r="${size/2 - 2}"
        fill="${color}"
        fill-opacity="${opacity}"
        stroke="white"
        stroke-width="2"
      />
    </svg>
  `;
  return 'data:image/svg+xml;base64,' + btoa(svg);
};

const CLUSTER_STYLES = [
  {
    textColor: 'white',
    textSize: 13,
    width: 40,
    height: 40,
    url: createSvgClusterIcon(40, '#596FB7', 0.95)
  },
  {
    textColor: 'white',
    textSize: 14,
    width: 50,
    height: 50,
    url: createSvgClusterIcon(50, '#11235A', 0.95)
  },
  {
    textColor: 'white',
    textSize: 16,
    width: 60,
    height: 60,
    url: createSvgClusterIcon(60, '#11235A', 1)
  }
];

const useMarkerClusterer = ({ 
  mapRef, 
  facilities, 
  setSelectedFacility,
  markerIcon
}) => {
  const clustererRef = useRef(null);

  useEffect(() => {
    if (!mapRef.current?.state?.map) return;

    const map = mapRef.current.state.map;

    if (clustererRef.current) {
      clustererRef.current.clearMarkers();
    }

    const validFacilityMarkers = facilities
      .filter(facility => 
        facility.fcltyCrdntLa !== 0 && 
        facility.fcltyCrdntLo !== 0
      )
      .map(facility => {
        const marker = new window.google.maps.Marker({
          position: {
            lat: Number(facility.fcltyCrdntLa),
            lng: Number(facility.fcltyCrdntLo)
          },
          icon: markerIcon
        });

        marker.addListener('click', () => {
          setSelectedFacility(facility);
        });

        return marker;
      });

    clustererRef.current = new MarkerClusterer({
      map,
      markers: validFacilityMarkers,
      gridSize: 60,
      maxZoom: 15,
      zIndex: 1,
      styles: CLUSTER_STYLES
    });

    return () => {
      if (clustererRef.current) {
        clustererRef.current.clearMarkers();
      }
    };
  }, [facilities, mapRef, setSelectedFacility, markerIcon]);

  return clustererRef;
};

export default useMarkerClusterer;
