// components/ChatContainer.js
import React, { useState } from 'react';

const ChatContainer = () => {
  const [messages, setMessages] = useState([
    { id: 1, type: 'assistant', content: '안녕하세요! AI 누리입니다. 실시간으로 유익한 맞춤형 답변을 제공해요' }
  ]);
  const [inputMessage, setInputMessage] = useState('');

  const handleSendMessage = () => {
    if (!inputMessage.trim()) return;

    setMessages([...messages, { id: Date.now(), type: 'user', content: inputMessage }]);
    setInputMessage('');
  };

  return (
    <div className="chat-container">
      <div className="chat-messages">
        {messages.map(message => (
          <div key={message.id} className={`message-bubble ${message.type}`}>
            {message.content}
          </div>
        ))}
      </div>
      <div className="chat-input-container">
        <input
          type="text"
          value={inputMessage}
          onChange={(e) => setInputMessage(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
          placeholder="메시지를 입력하세요"
          className="chat-input"
        />
        <button 
          onClick={handleSendMessage}
          className="chat-send-button"
          disabled={!inputMessage.trim()}
        >
          전송
        </button>
      </div>
    </div>
  );
};

export default ChatContainer;
