// App.js
import React, { useState } from 'react';
import { GoogleMap, LoadScript, MarkerF } from '@react-google-maps/api'; // MarkerF로 변경
import LoadingSpinner from './components/LoadingSpinner.js';
import FacilityCard from './components/FacilityCard';
import ErrorMessage from './components/ErrorMessage';
import './App.css';

const GOOGLE_MAPS_API_KEY = 'AIzaSyBR3XR176Wj4TBWWcH2qY_tungtmqpofVw';

const App = () => {
  // ... 기존 state와 style 코드 유지 ...

  // 주변 시설 정보 가져오기 API 호출
  const fetchNearbyFacilities = async (location) => {
    try {
      const requestData = {
        fcltyCrdntLo: location.lng,
        fcltyCrdntLa: location.lat
      };

      console.log('Request Data:', requestData);

      const response = await fetch('http://3.35.98.48:8080/facilities', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify(requestData)
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Server error response:', errorText);
        throw new Error('서버에서 데이터를 가져오는데 실패했습니다.');
      }

      const data = await response.json();
      console.log('Received data:', data);
      setFacilities(data);
    } catch (error) {
      console.error('API Error:', error);
      setError("시설 정보를 가져오는데 실패했습니다.");
      setFacilities([]);
    }
  };

  return (
    <div className="app-container">
      <header className="search-bar">
        <label htmlFor="search-input" className="search-label">
          시설명 혹은 주소 검색
        </label>
        <input 
          type="text" 
          id="search-input"
          name="search"
          placeholder="시설명 혹은 주소 검색"
          autoComplete="off"
        />
      </header>

      {loading && <LoadingSpinner />}
      {error && <ErrorMessage message={error} onClose={() => setError(null)} />}

      <button
        className="my-location-button"
        onClick={getCurrentLocation}
        disabled={loading}
      >
        내 위치 보기
      </button>

      <LoadScript googleMapsApiKey={GOOGLE_MAPS_API_KEY}>
        <GoogleMap
          mapContainerStyle={mapStyles}
          zoom={15}
          center={center}
          onClick={() => setSelectedFacility(null)}
          options={{
            zoomControl: true,
            zoomControlOptions: {
              position: 9
            }
          }}
        >
          {userLocation && (
            <MarkerF 
              position={userLocation} 
              icon={userLocationIcon}
            />
          )}

          {facilities.map((facility) => (
            <MarkerF
              key={facility.id}
              position={{
                lat: facility.fcltyCrdntLa,
                lng: facility.fcltyCrdntLo
              }}
              icon={facilityIcon}
              onClick={() => setSelectedFacility(facility)}
            />
          ))}
        </GoogleMap>
      </LoadScript>

      {selectedFacility && (
        <FacilityCard
          facility={selectedFacility}
          onClose={() => setSelectedFacility(null)}
        />
      )}
    </div>
  );
};

export default App;
