import React from 'react';
import { GoogleMap, LoadScript, MarkerF } from '@react-google-maps/api';
import LoadingSpinner from './components/LoadingSpinner';
import FacilityCard from './components/FacilityCard';
import ErrorMessage from './components/ErrorMessage';
import SearchPanel from './components/SearchPanel';
import { useMapState } from './hooks/useMapState';
import { useSearch } from './hooks/useSearch';
import { fetchNearbyFacilities, fetchSuggestions } from './services/api';
import { GOOGLE_MAPS_API_KEY, MAP_STYLES, MAP_ICONS } from './constants/config';
import './App.css';

const App = () => {
  const mapState = useMapState();
  const { 
    center, setCenter, facilities, setFacilities, 
    userLocation, setUserLocation, loading, setLoading, 
    error, setError, selectedFacility, setSelectedFacility, 
    mapRef 
  } = mapState;

  const search = useSearch({ onSearch: handleSearch });
  const { 
    searchText, setSearchText, suggestions, setSuggestions, 
    showSearchPanel, setShowSearchPanel, searchPanelRef 
  } = search;

  const getCurrentLocation = async () => {
    setLoading(true);
    setError(null);
    try {
      const position = await new Promise((resolve, reject) => {
        navigator.geolocation.getCurrentPosition(resolve, reject);
      });

      const location = {
        lat: position.coords.latitude,
        lng: position.coords.longitude
      };
      setUserLocation(location);
      setCenter(location);
      const data = await fetchNearbyFacilities(location);
      setFacilities(data);
    } catch (error) {
      setError("위치 정보를 가져올 수 없습니다. 위치 서비스가 활성화되어 있는지 확인해주세요.");
    } finally {
      setLoading(false);
    }
  };

  const searchCurrentLocation = async () => {
    if (!mapRef.current?.state?.map) return;

    setLoading(true);
    setError(null);
    try {
      const map = mapRef.current.state.map;
      const center = map.getCenter();
      const location = {
        lat: center.lat(),
        lng: center.lng()
      };

      const data = await fetchNearbyFacilities(location);
      setFacilities(data);
    } catch (error) {
      setError("현재 지도 위치에서 시설을 검색하는데 실패했습니다.");
    } finally {
      setLoading(false);
    }
  };

  const handleSelectFacility = (facility) => {
    const selectedLocation = {
      lat: facility.fcltyCrdntLa,
      lng: facility.fcltyCrdntLo
    };
    setSearchText(facility.fcltyNm);
    setCenter(selectedLocation);
    setSelectedFacility(facility);
    setShowSearchPanel(false);

    // 지도 이동 수정된 부분
    if (mapRef.current?.state?.map) {
      const map = mapRef.current.state.map;
      map.panTo(selectedLocation);
      map.setZoom(15); // 적절한 줌 레벨 설정
    }
  };

  useEffect(() => {
    const debounceTimer = setTimeout(() => {
      if (showSearchPanel && searchText.trim()) {
        fetchSuggestions(searchText, center)
          .then(setSuggestions)
          .catch(console.error);
      }
    }, 300);

    return () => clearTimeout(debounceTimer);
  }, [searchText, showSearchPanel, center]);

  return (
    <div className="app-container">
      <div className="search-container" ref={searchPanelRef}>
        <div className="search-bar">
          <input 
            type="text" 
            value={searchText}
            onChange={(e) => setSearchText(e.target.value)}
            onFocus={() => setShowSearchPanel(true)}
            placeholder="시설명 혹은 주소 검색"
            className="search-input"
          />
        </div>

        {showSearchPanel && (
          <SearchPanel
            suggestions={suggestions}
            onSelectFacility={handleSelectFacility}
            onSelectItem={(item) => {
              setSearchText(item);
              handleSearch('item');
              setShowSearchPanel(false);
            }}
            searchText={searchText}
            setSearchText={setSearchText}
          />
        )}
      </div>

      {loading && <LoadingSpinner />}
      {error && <ErrorMessage message={error} onClose={() => setError(null)} />}

      <div className="button-container">
        <button
          className="location-button"
          onClick={getCurrentLocation}
          disabled={loading}
        >
          내 위치 보기
        </button>
        <button
          className="location-button"
          onClick={searchCurrentLocation}
          disabled={loading}
        >
          현재 위치 검색
        </button>
      </div>

      <LoadScript googleMapsApiKey={GOOGLE_MAPS_API_KEY}>
        <GoogleMap
          mapContainerStyle={MAP_STYLES}
          zoom={15}
          center={center}
          onClick={() => {
            setSelectedFacility(null);
            setShowSearchPanel(false);
          }}
          options={{
            zoomControl: true,
            zoomControlOptions: {
              position: 9
            }
          }}
          ref={mapRef}
        >
          {userLocation && (
            <MarkerF 
              position={userLocation} 
              icon={MAP_ICONS.user} 
            />
          )}

          {facilities.map((facility) => (
            <MarkerF
              key={facility.id}
              position={{
                lat: facility.fcltyCrdntLa,
                lng: facility.fcltyCrdntLo
              }}
              icon={MAP_ICONS.facility}
              onClick={() => setSelectedFacility(facility)}
            />
          ))}
        </GoogleMap>
      </LoadScript>

      {selectedFacility && (
        <FacilityCard
          facility={selectedFacility}
          onClose={() => setSelectedFacility(null)}
        />
      )}
    </div>
  );
};

export default App;
