// src/components/FacilityCard.js
import React from 'react';
import { formatDistance } from '../utils/formatters';
import './FacilityCard.css';

const FacilityCard = ({ facility, onClose, userLocation }) => {
  let distanceText = '';
  if (userLocation && facility.fcltyCrdntLa && facility.fcltyCrdntLo) {
    if (facility.distance) {
      distanceText = formatDistance(facility.distance);
    }
  }

  return (
    <div className="facility-card">
      <button className="close-button" onClick={onClose}>&times;</button>
      <h3>{facility.fcltyNm}</h3>
      <p className="address">{facility.fcltyAddr} {facility.fcltyDetailAddr}</p>
      {facility.rprsntvTelNo && (
        <p className="phone">
          <a href={`tel:${facility.rprsntvTelNo}`}>{facility.rprsntvTelNo}</a>
        </p>
      )}
      {facility.mainItemNm && (
        <p className="main-item">{facility.mainItemNm}</p>
      )}
      {distanceText && (
        <p className="distance">현재 위치에서 {distanceText}</p>
      )}
    </div>
  );
};

export default FacilityCard;
