import React, { useState, useEffect, useRef } from 'react';
import { GoogleMap, LoadScript, MarkerF } from '@react-google-maps/api';
import LoadingSpinner from './components/LoadingSpinner';
import FacilityCard from './components/FacilityCard';
import ErrorMessage from './components/ErrorMessage';
import './App.css';

const GOOGLE_MAPS_API_KEY = 'AIzaSyBR3XR176Wj4TBWWcH2qY_tungtmqpofVw';

const App = () => {
  const [center, setCenter] = useState({
    lat: 37.5665,
    lng: 126.9780
  });
  const [facilities, setFacilities] = useState([]);
  const [userLocation, setUserLocation] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedFacility, setSelectedFacility] = useState(null);
  const [searchText, setSearchText] = useState('');
  const [suggestions, setSuggestions] = useState(null);
  const [showSearchPanel, setShowSearchPanel] = useState(false);
  const searchPanelRef = useRef(null);
  const mapRef = useRef(null);

  const mapStyles = {
    height: "100vh",
    width: "100%"
  };

  // Map marker icons remain the same...

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchPanelRef.current && !searchPanelRef.current.contains(event.target)) {
        setShowSearchPanel(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  // Updated handleSearch function to handle facility selection
  const handleFacilitySelect = (facility) => {
    const newCenter = {
      lat: facility.fcltyCrdntLa,
      lng: facility.fcltyCrdntLo
    };

    setCenter(newCenter);
    setSelectedFacility(facility);
    setShowSearchPanel(false);
    setSearchText(facility.fcltyNm);

    // Update facilities array to include the selected facility
    if (!facilities.some(f => f.id === facility.id)) {
      setFacilities(prev => [...prev, facility]);
    }

    // Smoothly move the map to the new location
    if (mapRef.current) {
      const map = mapRef.current.state.map;
      map.panTo(newCenter);
      map.setZoom(15); // Optional: set zoom level
    }
  };

  // Other functions remain the same...

  return (
    <div className="app-container">
      <div className="search-container" ref={searchPanelRef}>
        <div className="search-bar">
          <input 
            type="text" 
            value={searchText}
            onChange={(e) => setSearchText(e.target.value)}
            onFocus={() => setShowSearchPanel(true)}
            placeholder="시설명 혹은 주소 검색"
            className="search-input"
          />
        </div>

        {showSearchPanel && (
          <div className="search-panel">
            {suggestions?.mainItems && suggestions.mainItems.length > 0 && (
              <div className="search-section">
                <h3 className="text-sm font-semibold mb-2">종목</h3>
                {suggestions.mainItems.map((item, index) => (
                  <div
                    key={index}
                    className="search-item flex items-center p-2 hover:bg-gray-100 cursor-pointer"
                    onClick={() => {
                      setSearchText(item);
                      handleSearch('item');
                      setShowSearchPanel(false);
                    }}
                  >
                    <span className="material-icons mr-2">sports</span>
                    {item}
                  </div>
                ))}
              </div>
            )}

            {suggestions?.facilities && suggestions.facilities.length > 0 && (
              <div className="search-section">
                <h3 className="text-sm font-semibold mb-2">시설명</h3>
                {suggestions.facilities.map((facility) => (
                  <div
                    key={facility.id}
                    className="search-item flex items-center p-2 hover:bg-gray-100 cursor-pointer"
                    onClick={() => handleFacilitySelect(facility)}
                  >
                    <span className="material-icons mr-2">location_on</span>
                    <div>
                      <div>{facility.fcltyNm}</div>
                      <div className="text-sm text-gray-500">{Math.round(facility.distance)}m</div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>

      {/* LoadingSpinner and ErrorMessage components remain the same... */}

      <LoadScript googleMapsApiKey={GOOGLE_MAPS_API_KEY}>
        <GoogleMap
          mapContainerStyle={mapStyles}
          zoom={15}
          center={center}
          onClick={() => {
            setSelectedFacility(null);
            setShowSearchPanel(false);
          }}
          options={{
            zoomControl: true,
            zoomControlOptions: {
              position: 9
            }
          }}
          ref={mapRef}
        >
          {userLocation && <MarkerF position={userLocation} icon={userLocationIcon} />}

          {facilities.map((facility) => (
            <MarkerF
              key={facility.id}
              position={{
                lat: facility.fcltyCrdntLa,
                lng: facility.fcltyCrdntLo
              }}
              icon={facilityIcon}
              onClick={() => setSelectedFacility(facility)}
            />
          ))}
        </GoogleMap>
      </LoadScript>

      {selectedFacility && (
        <FacilityCard
          facility={selectedFacility}
          onClose={() => setSelectedFacility(null)}
        />
      )}
    </div>
  );
};

export default App;
