import React, { useState, useEffect, useRef } from 'react';
import { Search } from 'lucide-react';

const SearchBar = ({ userLocation, onSearchResult, setCenter, setSelectedFacility }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState({ mainItems: [], facilities: [] });
  const [isSearching, setIsSearching] = useState(false);
  const [searchMode, setSearchMode] = useState('facility'); // 'facility' or 'item'
  const searchTimeout = useRef(null);
  const searchRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) {
        setSuggestions({ mainItems: [], facilities: [] });
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const fetchSuggestions = async (term) => {
    if (!term.trim()) {
      setSuggestions({ mainItems: [], facilities: [] });
      return;
    }

    try {
      const response = await fetch('/suggestions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          fcltyCrdntLo: userLocation?.lng || 0,
          fcltyCrdntLa: userLocation?.lat || 0,
          searchText: term
        })
      });

      if (!response.ok) throw new Error('자동완성 검색 중 오류가 발생했습니다.');

      const data = await response.json();
      setSuggestions(data);
    } catch (error) {
      console.error('Suggestion error:', error);
      setSuggestions({ mainItems: [], facilities: [] });
    }
  };

  const handleSearch = async (term, mode = 'facility') => {
    if (!term.trim()) return;

    setIsSearching(true);
    try {
      const url = mode === 'facility' ? '/search' : '/search-item';
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          fcltyCrdntLo: userLocation?.lng || 0,
          fcltyCrdntLa: userLocation?.lat || 0,
          searchText: term
        })
      });

      if (!response.ok) throw new Error('검색 중 오류가 발생했습니다.');

      const data = await response.json();
      if (onSearchResult) {
        onSearchResult(data);
      }
      setSuggestions({ mainItems: [], facilities: [] });
    } catch (error) {
      console.error('Search error:', error);
    } finally {
      setIsSearching(false);
    }
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    if (searchTimeout.current) {
      clearTimeout(searchTimeout.current);
    }

    searchTimeout.current = setTimeout(() => {
      fetchSuggestions(value);
    }, 300);
  };

  const handleSuggestionClick = async (suggestion, type) => {
    if (type === 'mainItem') {
      setSearchTerm(suggestion);
      handleSearch(suggestion, 'item');
      setSearchMode('item');
    } else {
      setSearchTerm(suggestion.fcltyNm);
      handleSearch(suggestion.fcltyNm, 'facility');
      setSearchMode('facility');
    }
  };

  return (
    <div className="relative w-full" ref={searchRef}>
      <div className="relative flex items-center">
        <Search className="absolute left-3 text-gray-400" size={20} />
        <input
          type="text"
          className="w-full pl-10 pr-4 py-2 border border-gray-200 rounded-lg focus:outline-none focus:border-blue-500"
          placeholder="시설명 혹은 주소 검색"
          value={searchTerm}
          onChange={handleInputChange}
          onKeyPress={(e) => {
            if (e.key === 'Enter') {
              handleSearch(searchTerm, searchMode);
            }
          }}
          autoComplete="off"
        />
      </div>

      {(suggestions.mainItems.length > 0 || suggestions.facilities.length > 0) && (
        <div className="absolute w-full mt-2 bg-white rounded-lg shadow-lg border border-gray-200 max-h-96 overflow-y-auto z-50">
          {suggestions.mainItems.length > 0 && (
            <div className="p-2 bg-gray-50 text-sm text-gray-600">종목</div>
          )}
          {suggestions.mainItems.map((item, index) => (
            <div
              key={`item-${index}`}
              className="p-3 hover:bg-gray-50 cursor-pointer border-b border-gray-100"
              onClick={() => handleSuggestionClick(item, 'mainItem')}
            >
              <div className="font-medium">{item}</div>
            </div>
          ))}

          {suggestions.facilities.length > 0 && (
            <div className="p-2 bg-gray-50 text-sm text-gray-600">시설</div>
          )}
          {suggestions.facilities.map((facility) => (
            <div
              key={facility.id}
              className="p-3 hover:bg-gray-50 cursor-pointer border-b border-gray-100 last:border-none"
              onClick={() => handleSuggestionClick(facility, 'facility')}
            >
              <div className="font-medium">{facility.fcltyNm}</div>
              {facility.distance && (
                <div className="text-sm text-blue-500 mt-1">
                  {facility.distance}m
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SearchBar;
